import React, { useState, useEffect } from "react";
import "./../App.css"
import axios from "axios";

function LoginPage(){
    const [loginId, setLoginId] = useState("");
    const [password, setPassword] = useState("");

    const getLoginId = (e) => {
        setLoginId(e.target.value)
        console.log("id :: " + e.target.value);
    }
    const getPassword = (e) => {
        setPassword(e.target.value)
        console.log("pwd :: " + e.target.value);
    }



    const login = () => {
        axios.post('http://localhost:8888/loginUser', {
            userId : loginId,
            userPwd : password
        }, {
            headers : {
                "Content-Type" : "application/json"
            }
        })
            .then(res => console.log("결과값 :: " + JSON.stringify(res.data)))
            .catch()
    };



    return(
        <div className="login-page">
            <div className="login-form">
                <h1 className="login-header">Log In</h1>
                <form >
                    <label className="input-label" htmlFor="email">

                    </label>
                    <input className="input-field" placeholder='Email or Phone' type='text' name={loginId} value={loginId} onChange={getLoginId}/><br/><br/>
                    <label className="input-label" htmlFor="password">
                        Password
                    </label>
                    <input className="input-field" type='text' name={password} value={password} onChange={getPassword}/><br/><br/>
                    <button className="login-button" type="submit" >로그인</button>
                </form>
            </div>

            <div className="forgot-password">
                <a href="#">Forgot Password?</a>
            </div>
            <hr />
            <div className="create-account">
                <a href="#">Create New Account</a>
            </div>
        </div>
    )
}
export default LoginPage;
